import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function BankerRegister() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();
        setMessage('');

        try {
            const res = await fetch('/api/banker/register', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ email, password })
})


            if (!res.ok) {
                const err = await res.json().catch(() => ({}));
                throw new Error(err.message || 'Registration failed');
            }

            setMessage('✅ Banker registered successfully! Redirecting to login...');
            setTimeout(() => navigate('/banker-login'), 1500);

        } catch (err) {
            setMessage(`❌ ${err.message}`);
        }
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <h2>Banker Registration</h2>
            <form onSubmit={handleRegister} style={{ display: 'inline-block', textAlign: 'left' }}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    style={{ display: 'block', marginBottom: '10px', padding: '8px' }}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    style={{ display: 'block', marginBottom: '10px', padding: '8px' }}
                />
                <button type="submit" style={{ padding: '8px 15px' }}>Register</button>
            </form>
            {message && <p style={{ marginTop: '10px' }}>{message}</p>}
        </div>
    );
}
